import{_ as t,c as s,o as a,ah as r}from"./chunks/framework.DqD713j2.js";const N=JSON.parse('{"title":"Nest框架","description":"","frontmatter":{},"headers":[],"relativePath":"Document/Node后端框架开发/Nest框架/Nest框架介绍和相关网址资源.md","filePath":"Document/Node后端框架开发/Nest框架/Nest框架介绍和相关网址资源.md","lastUpdated":1750954157000}'),n={name:"Document/Node后端框架开发/Nest框架/Nest框架介绍和相关网址资源.md"};function l(o,e,i,h,c,p){return a(),s("div",null,e[0]||(e[0]=[r('<h1 id="nest框架" tabindex="-1">Nest框架 <a class="header-anchor" href="#nest框架" aria-label="Permalink to &quot;Nest框架&quot;">​</a></h1><h2 id="nest-js框架介绍" tabindex="-1">Nest.js框架介绍 <a class="header-anchor" href="#nest-js框架介绍" aria-label="Permalink to &quot;Nest.js框架介绍&quot;">​</a></h2><p>Nest (NestJS) 是一个用于构建高效、可扩展的 Node.js 服务器端应用程序的开发框架。</p><p>它利用 JavaScript 的渐进增强的能力，使用并完全支持 TypeScript （仍然允许开发者使用纯 JavaScript 进行开发），并结合了 OOP （面向对象编程）、FP （函数式编程）和 FRP （函数响应式编程）。</p><p>在底层，Nest 构建在强大的 HTTP 服务器框架上，例如 <strong>Express</strong> 框架（默认配置），并且还可以通过配置从而使用 <strong>Fastify</strong> 框架。</p><p>Nest 在这些常见的 Node.js 框架 (Express/Fastify) 之上提高了一个抽象级别，但仍然向开发者直接暴露了底层框架的 API。这使得开发者可以自由地使用适用于底层平台的无数的第三方模块。</p><ul><li>Nest (NestJS) 是一个用于构建高效、可扩展的 Node.js 服务器端应用程序的开发框架。它利用 JavaScript 的渐进增强的能力，使用并完全支持 TypeScript （仍然允许开发者使用纯 JavaScript 进行开发），并结合了 OOP （面向对象编程）、FP （函数式编程）和 FRP （函数响应式编程）。</li><li>在底层，Nest 构建在强大的 HTTP 服务器框架上，例如 Express （默认），并且还可以通过配置从而使用 Fastify ！</li><li>Nest 在这些常见的 Node.js 框架 (Express/Fastify) 之上提高了一个抽象级别，但仍然向开发者直接暴露了底层框架的 API。这使得开发者可以自由地使用适用于底层平台的无数的第三方模块。</li></ul><h2 id="nest网址文档" tabindex="-1">Nest网址文档 <a class="header-anchor" href="#nest网址文档" aria-label="Permalink to &quot;Nest网址文档&quot;">​</a></h2><h3 id="官方资源" tabindex="-1">官方资源 <a class="header-anchor" href="#官方资源" aria-label="Permalink to &quot;官方资源&quot;">​</a></h3><ul><li>Nest官方网站：<a href="https://nestjs.com/" target="_blank" rel="noreferrer">https://nestjs.com/</a></li><li>Nest官方文档：<a href="https://docs.nestjs.com/" target="_blank" rel="noreferrer">https://docs.nestjs.com/</a></li><li>Nest开源地址GitHub：<a href="https://github.com/nestjs/nest" target="_blank" rel="noreferrer">https://github.com/nestjs/nest</a></li><li>Nest GitHub Repo：<a href="https://github.com/nestjs/nest" target="_blank" rel="noreferrer">https://github.com/nestjs/nest</a></li></ul><h3 id="nest中文文档" tabindex="-1">Nest中文文档 <a class="header-anchor" href="#nest中文文档" aria-label="Permalink to &quot;Nest中文文档&quot;">​</a></h3><ul><li>Nest中文文档1：<a href="https://nestjs.bootcss.com/" target="_blank" rel="noreferrer">https://nestjs.bootcss.com/</a></li><li>Nest中文文档2：<a href="https://docs.nestjs.cn/" target="_blank" rel="noreferrer">https://docs.nestjs.cn/</a></li><li>资源：<a href="https://docs.nestjs.cn/8/awesome" target="_blank" rel="noreferrer">https://docs.nestjs.cn/8/awesome</a></li><li><a href="https://nestjs.inode.club/" target="_blank" rel="noreferrer">中文文档 | NestJS - 一个渐进式的Node.js框架 (inode.club)</a></li></ul><h3 id="学习" tabindex="-1">学习 <a class="header-anchor" href="#学习" aria-label="Permalink to &quot;学习&quot;">​</a></h3><ul><li><a href="https://blog.csdn.net/xgangzai/article/details/109252492" target="_blank" rel="noreferrer">https://blog.csdn.net/xgangzai/article/details/109252492</a></li><li><a href="https://zhuanlan.zhihu.com/p/71987482" target="_blank" rel="noreferrer">https://zhuanlan.zhihu.com/p/71987482</a></li></ul><hr>',15)]))}const _=t(n,[["render",l]]);export{N as __pageData,_ as default};
