import{_ as i,c as l,o as e,ah as r}from"./chunks/framework.DqD713j2.js";const t="/FrontEndLearnNotes/assets/Windows%E7%B3%BB%E7%BB%9F%E6%9E%B6%E6%9E%84.Cu8FqWGV.jpg",b=JSON.parse('{"title":"从软件到硬件","description":"","frontmatter":{},"headers":[],"relativePath":"Document/从软件到硬件/从软件到硬件.md","filePath":"Document/从软件到硬件/从软件到硬件.md","lastUpdated":1750954157000}'),n={name:"Document/从软件到硬件/从软件到硬件.md"};function o(p,a,s,h,d,u){return e(),l("div",null,a[0]||(a[0]=[r('<h1 id="从软件到硬件" tabindex="-1">从软件到硬件 <a class="header-anchor" href="#从软件到硬件" aria-label="Permalink to &quot;从软件到硬件&quot;">​</a></h1><h2 id="认识更多种类的计算机" tabindex="-1">认识更多种类的计算机 <a class="header-anchor" href="#认识更多种类的计算机" aria-label="Permalink to &quot;认识更多种类的计算机&quot;">​</a></h2><ul><li>PC机</li><li>小型机</li><li>超级计算机</li><li>单片机</li><li>单板机</li></ul><p>概念</p><p>光子计算机</p><p>量子计算机</p><h2 id="软件与硬件的异同" tabindex="-1">软件与硬件的异同 <a class="header-anchor" href="#软件与硬件的异同" aria-label="Permalink to &quot;软件与硬件的异同&quot;">​</a></h2><p>结构角度</p><p>开发角度</p><p>维护角度</p><p>第三种概念一固件</p><h2 id="单片机与传感器-嵌入式" tabindex="-1">单片机与传感器(嵌入式) <a class="header-anchor" href="#单片机与传感器-嵌入式" aria-label="Permalink to &quot;单片机与传感器(嵌入式)&quot;">​</a></h2><p>集成电路是现代计算机的基础</p><p>单片机与中央处理器不同</p><p>单片机的CPU叫微控制器</p><p>单片机通过引脚与其他芯片或设备连接</p><h2 id="单片机的指令集" tabindex="-1">单片机的指令集 <a class="header-anchor" href="#单片机的指令集" aria-label="Permalink to &quot;单片机的指令集&quot;">​</a></h2><p>x86_64指令集</p><p>ARM指令集</p><p>MIPS指令集</p><p>使用Node.js可以忽略指令集之间的差异</p><h2 id="单片机接口" tabindex="-1">单片机接口 <a class="header-anchor" href="#单片机接口" aria-label="Permalink to &quot;单片机接口&quot;">​</a></h2><p>GPIO：Inter-Integrated Circuit通用输入/输出接口</p><p>I^2C：Inter-Integrated Circuit 集成电路总线</p><p>UART：Universal Asynchronous Receiver/Transmitter通用异步收发传输器</p><p>PWM：Pulse Width Modulation脉冲宽度调制</p><p>ADC：Analog-to-Digital converter 模/数转换器</p><h2 id="外部设备" tabindex="-1">外部设备 <a class="header-anchor" href="#外部设备" aria-label="Permalink to &quot;外部设备&quot;">​</a></h2><p>输入设备</p><p>输出设备</p><p>存储设备</p><p>传感器属于输入设备，相当于人类的感觉器官</p><ul><li>按钮</li><li>继电器</li><li>LED模块</li><li>LCD模块</li><li>温湿度传感器</li><li>光照传感器</li><li>红外发送模块</li><li>红外接收模块</li><li>声音传感器</li><li>蜂鸣器</li></ul><p>案例：智能气象站</p><h3 id="中央处理器" tabindex="-1">中央处理器 <a class="header-anchor" href="#中央处理器" aria-label="Permalink to &quot;中央处理器&quot;">​</a></h3><ul><li>运算器：算术、逻辑算术逻辑单元、累加器寄存器、数据总线)</li><li>控制器：复位、使能计数器、指令寄存器、指令解码器、状态寄存器、时钟发生器、微操作信号发生器</li></ul><h2 id="cpu是怎样工作的" tabindex="-1">CPU是怎样工作的 <a class="header-anchor" href="#cpu是怎样工作的" aria-label="Permalink to &quot;CPU是怎样工作的&quot;">​</a></h2><p>图灵机</p><p>冯诺伊曼架构</p><p>哈弗架构</p><h2 id="计算机语言的本源与发展" tabindex="-1">计算机语言的本源与发展 <a class="header-anchor" href="#计算机语言的本源与发展" aria-label="Permalink to &quot;计算机语言的本源与发展&quot;">​</a></h2><ul><li>本源：二进制数据</li><li>第一代语言：机器指令(痛苦指数 五星)</li><li>第二代语言：汇编指令 (痛苦指数 四星半)</li><li>第三代语言：高级语言 (主要特征：面向过程)</li><li>第四代语言：面向对象语言 (面向互联网、天然支持数据库)</li></ul><h2 id="汇编指令入门" tabindex="-1">汇编指令入门 <a class="header-anchor" href="#汇编指令入门" aria-label="Permalink to &quot;汇编指令入门&quot;">​</a></h2><ul><li>汇编语言是最贴近底层的计算机语言</li><li>汇编语言是直接操作硬件的，没有任何抽象</li><li>汇编语言由指令与数据组成，没有任何语句</li><li>汇编指令受到硬件平台限制，可移植性很低</li><li>了解一些汇编语言知识，对理解计算机的运作机制和内存管理大有好处</li></ul><p>汇编指令例子</p><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki one-dark-pro vp-code" tabindex="0"><code><span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><h2 id="了解操作系统" tabindex="-1">了解操作系统 <a class="header-anchor" href="#了解操作系统" aria-label="Permalink to &quot;了解操作系统&quot;">​</a></h2><ul><li>屏蔽硬件物理特性</li><li>屏蔽操作细节</li><li>有效管理系统资源</li><li>提高系统资源使用效率</li></ul><h3 id="unix哲学" tabindex="-1">Unix哲学 <a class="header-anchor" href="#unix哲学" aria-label="Permalink to &quot;Unix哲学&quot;">​</a></h3><ul><li>小即是美</li><li>并行思考</li><li>分层设计</li><li>数据决定一切</li></ul><h3 id="linux系统架构" tabindex="-1">Linux系统架构 <a class="header-anchor" href="#linux系统架构" aria-label="Permalink to &quot;Linux系统架构&quot;">​</a></h3><p>几乎完整的Linux架构图</p><p>用户模式：</p><ul><li>应用程序 (sh、vi、OpenOffice.org等)</li><li>复杂函式库 (KDE、glib等)</li><li>简单函式库(opendbm、sin等)</li><li>C函式库（open、fopen、socket、exec、calloc等)</li></ul><p>内核模式：</p><ul><li>系统中断、呼叫、错误等软硬件讯息</li><li>内核(驱动程式、行程、网络、内存管理等)</li><li>硬件 (处理器、内存、各种装置)</li></ul><h3 id="windows系统架构" tabindex="-1">Windows系统架构 <a class="header-anchor" href="#windows系统架构" aria-label="Permalink to &quot;Windows系统架构&quot;">​</a></h3><p><img src="'+t+'" alt="Windows系统架构"></p>',58)]))}const m=i(n,[["render",o]]);export{b as __pageData,m as default};
