import{_ as a,c as e,o as r,ah as o}from"./chunks/framework.DqD713j2.js";const m=JSON.parse('{"title":"HTMX简介和相关网站","description":"","frontmatter":{},"headers":[],"relativePath":"Document/前端主流开发框架/HTMX/HTMX简介和相关网站.md","filePath":"Document/前端主流开发框架/HTMX/HTMX简介和相关网站.md","lastUpdated":1750954157000}'),s={name:"Document/前端主流开发框架/HTMX/HTMX简介和相关网站.md"};function i(n,t,h,l,p,_){return r(),e("div",null,t[0]||(t[0]=[o('<h1 id="htmx简介和相关网站" tabindex="-1">HTMX简介和相关网站 <a class="header-anchor" href="#htmx简介和相关网站" aria-label="Permalink to &quot;HTMX简介和相关网站&quot;">​</a></h1><p>JS新星：<a href="https://risingstars.js.org/2023/zh" target="_blank" rel="noreferrer">2023 JavaScript Rising Stars</a></p><h2 id="简介" tabindex="-1">简介 <a class="header-anchor" href="#简介" aria-label="Permalink to &quot;简介&quot;">​</a></h2><p>为谁而设：</p><ul><li>你希望减少JavaScript的编写量</li><li>你希望代码更简单，以超媒体为中心</li></ul><p>HTMX在2023年迅速走红，过去一年间在GitHub上赢得了大量星标。HTMX并非普通的JS框架。如果你使用HTMX，你将大部分时间都花在超媒体的世界中，以与我们通常对现代Web开发的JS密集型视角完全不同的视角看待Web开发。<strong>HTMX利用HATEOAS（Hypermedia作为应用程序状态的引擎）的概念，使开发人员能够直接从HTML访问浏览器功能，而不是使用Javascript。</strong></p><p>此外，它还证明了通过发布令人惊叹的表情符号并以口碑作为主要营销手段，你可以获得人气和认可。不仅如此，你还可能成为HTMX的CEO！它吸引了许多开发人员尝试这种构建网站的方法，并重新思考他们当前的实践。所有这些都使2024年对于这个库的未来发展充满了激动人心的可能性。</p><h2 id="相关网站" tabindex="-1">相关网站 <a class="header-anchor" href="#相关网站" aria-label="Permalink to &quot;相关网站&quot;">​</a></h2><p>开源地址：<a href="https://github.com/bigskysoftware/htmx" target="_blank" rel="noreferrer">https://github.com/bigskysoftware/htmx</a></p><p>官网：<a href="https://htmx.org/" target="_blank" rel="noreferrer">https://htmx.org/</a></p>',10)]))}const d=a(s,[["render",i]]);export{m as __pageData,d as default};
