import{_ as i,c as p,o as u,ah as e}from"./chunks/framework.DqD713j2.js";const h=JSON.parse('{"title":"uni-app开发","description":"","frontmatter":{},"headers":[],"relativePath":"Document/前端主流开发框架/Vue/uni-app开发.md","filePath":"Document/前端主流开发框架/Vue/uni-app开发.md","lastUpdated":1750954157000}'),a={name:"Document/前端主流开发框架/Vue/uni-app开发.md"};function t(n,l,o,r,s,d){return u(),p("div",null,l[0]||(l[0]=[e('<h1 id="uni-app开发" tabindex="-1">uni-app开发 <a class="header-anchor" href="#uni-app开发" aria-label="Permalink to &quot;uni-app开发&quot;">​</a></h1><h2 id="为什么要使用uni-app" tabindex="-1">为什么要使用uni-app <a class="header-anchor" href="#为什么要使用uni-app" aria-label="Permalink to &quot;为什么要使用uni-app&quot;">​</a></h2><ul><li><p>微应用</p><ul><li><p>微信小程序发布</p><ul><li><p>2017.1.9发布</p></li><li><p>小程序创业</p></li></ul></li><li><p>国民级应用普及</p><ul><li><p>支付宝</p></li><li><p>抖音</p></li><li><p>头条</p></li><li><p>百度app</p></li></ul></li><li><p>前端泛滥时代</p><ul><li>开发维护成本高</li></ul></li><li><p>未来微应用会成为趋势</p></li></ul></li><li><p>uniapp</p><ul><li><p>一套代码适配</p><ul><li><p>ios</p></li><li><p>Android</p></li><li><p>h5</p></li><li><p>各种小程序平台</p></li></ul></li><li><p>优点</p><ul><li><p>学习成本低</p><ul><li>基于Vue,微信小程序api <ul><li>使用Vue,小程序开发,学习成本几乎为零</li></ul></li></ul></li><li><p>开发成本低</p><ul><li>一套代码多端应用,满足初创公司的一切幻想</li></ul></li><li><p>按需编译</p><ul><li>通过注释写法,即可保留平台特点(微信卡包),又不增加其他平台发布资源包大写</li></ul></li></ul></li></ul></li></ul><h2 id="开发准备" tabindex="-1">开发准备 <a class="header-anchor" href="#开发准备" aria-label="Permalink to &quot;开发准备&quot;">​</a></h2><ul><li><p>编译工具下载</p><ul><li><p>HBuilderX</p><ul><li><a href="https://www.dcloud.io/" target="_blank" rel="noreferrer">https://www.dcloud.io/</a></li></ul></li><li><p>微信小程序开发工具</p><ul><li><a href="https://developers.weixin.qq.com/miniprogram/dev/devtools/devtools.html" target="_blank" rel="noreferrer">https://developers.weixin.qq.com/miniprogram/dev/devtools/devtools.html</a></li></ul></li><li><p>其他自行下载</p></li></ul></li></ul><h2 id="初探uinapp" tabindex="-1">初探uinApp <a class="header-anchor" href="#初探uinapp" aria-label="Permalink to &quot;初探uinApp&quot;">​</a></h2><ul><li><p>创建项目并且运行</p><ul><li><p>HBuilderX&gt;创建项目&gt;选择uinapp空模板</p><ul><li>参考官方提供demo</li></ul></li><li><p>微信开发者工具</p><ul><li>打开微信开发者工具&gt;设置&gt;安全设置&gt;打开服务端口</li></ul></li><li><p>HBuilderX&gt;运行&gt;小程序模拟器&gt;运行设置&gt;设置微信小程序开发工具路径</p></li><li><p>运行&gt;小程序模拟器&gt;启动</p></li></ul></li></ul><h2 id="目录介绍" tabindex="-1">目录介绍 <a class="header-anchor" href="#目录介绍" aria-label="Permalink to &quot;目录介绍&quot;">​</a></h2><ul><li><p>unpackage</p><ul><li>启动自动生成目录 <ul><li>会自动生成多版本代码</li></ul></li></ul></li><li><p>pages.json</p><ul><li><p>pages.json：通用部分配置项,页面路由、导航条、选项卡等页面类信息</p><ul><li>文档框架,globalStyle</li></ul></li><li><p>新增一个页面</p></li></ul></li><li><p>manifest.json</p><ul><li>项目常用配置 <ul><li>例如微信小程序AppId <ul><li>影响真机调试</li></ul></li></ul></li></ul></li><li><p>main.js</p><ul><li>项目入口文件</li></ul></li><li><p>App.js</p><ul><li>应用配置，用来配置App全局样式以及监听应用的生命周期</li></ul></li><li><p>static</p><ul><li>存放应用引用静态资源（如图片、视频等）的地方</li></ul></li><li><p>pages</p><ul><li><p>业务页面文件存放目录</p></li><li><p>添加路由</p><ul><li>配置页面</li></ul></li><li><p>配置系统底部导航</p></li></ul></li><li><p>uni.scss</p><ul><li><p>全局通用scss样式库</p><ul><li><p>不需要引入</p></li><li><p>style 加 lang=&quot;scss&quot;</p></li><li><p>安装scss编译插件</p></li></ul></li></ul></li><li><p>components</p><ul><li><p>新建目录</p><ul><li>名字默认写法</li></ul></li><li><p>兄弟组件通信</p><ul><li><p>事件订阅</p><ul><li><p>$on</p><ul><li>注册事件调用多次</li></ul></li><li><p>$once</p><ul><li>注册事件调用一次</li></ul></li><li><p>$emit</p><ul><li>触发事件</li></ul></li></ul></li></ul></li></ul></li></ul><h2 id="基础入门" tabindex="-1">基础入门 <a class="header-anchor" href="#基础入门" aria-label="Permalink to &quot;基础入门&quot;">​</a></h2><ul><li><p>表达式测试</p><ul><li><p>语法测试</p></li><li><p>表达式</p><ul><li><p>输出字符串,定义变量</p></li><li><p>支持运算符</p></li><li><p>逻辑运算符</p></li><li><p>二元运算符</p></li><li><p>js数据类型方法使用</p><ul><li>差异</li></ul></li></ul></li><li><p>不要在表达式里面写过多逻辑</p><ul><li>逻辑语句,switch,alert,等等</li></ul></li></ul></li><li><p>属性绑定</p><ul><li><p>小程序语法测试</p><ul><li>注意基于Vue语法 <ul><li>双引号,与双大括号之间有乾坤</li></ul></li></ul></li><li><p>:修饰符v-bind简写方式</p></li><li><p>动态样式</p><ul><li><p>class</p><ul><li><p>对象形式</p></li><li><p>数组形式</p></li><li><p>数组对象组合形式</p></li></ul></li><li><p>style</p><ul><li><p>对象形式</p></li><li><p>数组对象形式</p></li></ul></li></ul></li></ul></li><li><p>方法调用</p><ul><li><p>获取事件对象</p><ul><li>$event</li></ul></li><li><p>阻止事件冒泡</p><ul><li>修饰符</li></ul></li><li><p>uni-app里没有什么默认事件，比如 submit 并不会跳转页面</p></li></ul></li><li><p>常用指令</p><ul><li><p>v-show</p></li><li><p>v-if</p></li><li><p>v-for</p><ul><li><p>异步渲染</p></li><li><p>获取当前</p></li></ul></li></ul></li><li><p>API调用</p><ul><li><p>.nvue 是属于weex编写范畴,作用增强uinapp对原生部分支持</p></li><li><p>uinapp里面所有涉及Api调用统一参考,uinapi</p></li></ul></li><li><p>常用生命周期</p><ul><li><p>应用生命周期</p><ul><li><p>onLaunch</p><ul><li>可以获取场景值</li></ul></li><li><p>onShow</p><ul><li>进入应用触发</li></ul></li><li><p>onHide</p><ul><li>切入后台触发</li></ul></li></ul></li><li><p>分页生命周期</p><ul><li><p>onShow</p><ul><li>路由进入触发</li></ul></li><li><p>页面进入刷新一次</p><ul><li><p>onLoad</p><ul><li>页面初始化执行,用户页面获取参数</li></ul></li><li><p>onReady</p><ul><li>页面初次渲染完毕执行</li></ul></li></ul></li><li><p>执行多次</p><ul><li><p>onShow</p><ul><li>页面进入执行,执行多次</li></ul></li><li><p>onHide</p><ul><li>页面离开执行执行多次</li></ul></li></ul></li><li><p>监听窗口尺寸变化</p><ul><li>onResize</li></ul></li><li><p>下拉执行</p><ul><li>onPullDownRefresh</li></ul></li><li><p>页面滚动监听</p><ul><li>onPageScroll</li></ul></li><li><p>页面滚动到底执行</p><ul><li><p>onReachBottom</p></li><li><p>scroll-view组件有自己的方法</p></li></ul></li><li><p>点击系统配置tab触发</p><ul><li>onTabItemTap</li></ul></li><li><p>右上角分享设置</p><ul><li>onShareAppMessage</li></ul></li></ul></li><li><p>组件生命周期</p><ul><li><p>参照Vue生命周期</p><ul><li><p>h5项目支持</p><ul><li><p>beforeUpdate</p></li><li><p>updated</p></li></ul></li></ul></li><li><p>beforeCreate</p><ul><li>实例初始化执行</li></ul></li><li><p>created</p><ul><li>实例创建执行</li></ul></li><li><p>beforeMount</p><ul><li>模板渲染之前调用</li></ul></li><li><p>mounted</p><ul><li>模板渲染完毕</li></ul></li></ul></li></ul></li><li><p>路由跳转</p><ul><li><p>跳转非tab配置,并打开新页面</p><ul><li><p>uni.navigateTo</p><ul><li>保留当前页面 <ul><li>例如跳转详情页需要返回调用</li></ul></li></ul></li><li><p>uni.redirectTo</p><ul><li>不保留当前页面</li></ul></li><li><p>uni.navigateBack</p><ul><li>需要返回调用</li></ul></li><li><p>小程序中页面栈最多十层。使用uni.navigateTo频繁切换,会导致栈溢出,跳转失败</p></li></ul></li><li><p>跳转tab配置页面</p><ul><li>uni.switchTab(OBJECT)</li></ul></li><li><p>url</p><ul><li>携带参数</li></ul></li></ul></li><li><p>视图容器</p><ul><li><p>scroll-view</p><ul><li><p>横滑效果</p></li><li><p>点击切换效果</p></li></ul></li></ul></li><li><p>节点操作</p><ul><li><p>uni.createSelectorQuery().select(&#39;#test&#39;).boundingClientRect(function(e){</p></li><li><p>不能使用标准HTML标签，也不能用js对dom进行操作</p></li><li><p>差异</p><ul><li><p>没有window对象</p></li><li><p>也没有键盘修饰符</p></li></ul></li><li><p>onReady</p></li></ul></li><li><p>uni_app_ajax</p><ul><li><p>不检查http</p></li><li><p>设置请求头</p></li></ul></li><li><p>扩展 nui-app使用</p></li><li><p>地图调用</p></li></ul>',11)]))}const m=i(a,[["render",t]]);export{h as __pageData,m as default};
