import{_ as a,c as t,o as s,ah as o}from"./chunks/framework.DqD713j2.js";const p=JSON.parse('{"title":"GitHub的issue博客框架Gmeek","description":"","frontmatter":{},"headers":[],"relativePath":"Document/搭建在线博客或文档网站/GitHub的issue博客框架Gmeek.md","filePath":"Document/搭建在线博客或文档网站/GitHub的issue博客框架Gmeek.md","lastUpdated":1750954157000}'),r={name:"Document/搭建在线博客或文档网站/GitHub的issue博客框架Gmeek.md"};function i(n,e,l,h,c,u){return s(),t("div",null,e[0]||(e[0]=[o('<h1 id="github的issue博客框架gmeek" tabindex="-1">GitHub的issue博客框架Gmeek <a class="header-anchor" href="#github的issue博客框架gmeek" aria-label="Permalink to &quot;GitHub的issue博客框架Gmeek&quot;">​</a></h1><h2 id="简介" tabindex="-1">简介 <a class="header-anchor" href="#简介" aria-label="Permalink to &quot;简介&quot;">​</a></h2><p><a href="https://github.com/Meekdai/Gmeek" target="_blank" rel="noreferrer">Gmeek</a> 一个博客框架，超轻量级个人博客模板，完全基于<code>Github Pages </code>、 <code>Github Issues</code> 和 <code>Github Actions</code>，可以称作<code>All in Github</code>。不需要本地部署，从搭建到写作，只需要18秒，2步搭建好博客，第3步就是写作。</p><h2 id="网址" tabindex="-1">网址 <a class="header-anchor" href="#网址" aria-label="Permalink to &quot;网址&quot;">​</a></h2><p><strong>Gmeek的Github地址</strong>： <a href="https://github.com/Meekdai/Gmeek" target="_blank" rel="noreferrer">https://github.com/Meekdai/Gmeek</a></p><p><strong>Gmeek的文档</strong>：<a href="https://blog.meekdai.com/" target="_blank" rel="noreferrer">https://blog.meekdai.com/</a></p><h2 id="搭建安装和配置" tabindex="-1">搭建安装和配置 <a class="header-anchor" href="#搭建安装和配置" aria-label="Permalink to &quot;搭建安装和配置&quot;">​</a></h2><p>文档：<a href="https://blog.meekdai.com/post/Gmeek-kuai-su-shang-shou.html" target="_blank" rel="noreferrer">Gmeek快速上手 (meekdai.com)</a></p><p>视频教程：<a href="https://www.bilibili.com/video/BV1GM4m1m7ZD" target="_blank" rel="noreferrer">https://www.bilibili.com/video/BV1GM4m1m7ZD</a></p><h2 id="部署和写文章步骤" tabindex="-1">部署和写文章步骤 <a class="header-anchor" href="#部署和写文章步骤" aria-label="Permalink to &quot;部署和写文章步骤&quot;">​</a></h2><ul><li>博客主体使用 <strong>Github Pages</strong> 托管的静态页面</li><li>构建部署使用 <strong>Github Action</strong></li><li>写文章则使用 <strong>Github Issue</strong> 的富文本编辑器。</li></ul><h2 id="问题解决" tabindex="-1">问题解决 <a class="header-anchor" href="#问题解决" aria-label="Permalink to &quot;问题解决&quot;">​</a></h2><h3 id="更改config-json后-标题显示为空-头像可以更换-要怎么解决呀" tabindex="-1">更改config.json后，标题显示为空，头像可以更换，要怎么解决呀 <a class="header-anchor" href="#更改config-json后-标题显示为空-头像可以更换-要怎么解决呀" aria-label="Permalink to &quot;更改config.json后，标题显示为空，头像可以更换，要怎么解决呀&quot;">​</a></h3><p>通过Actions-&gt;build Gmeek-&gt;Run workflow-&gt;里面的按钮全局重新生成一次</p><p>把文件上传到Github的API：</p><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki one-dark-pro vp-code" tabindex="0"><code><span class="line"><span style="color:#61AFEF;">https://api.github.com/repos/</span><span style="color:#98C379;">{仓库所有者}</span><span style="color:#61AFEF;">/</span><span style="color:#98C379;">{仓库名称}</span><span style="color:#61AFEF;">/contents/</span><span style="color:#98C379;">{文件目录+文件名}</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div>',16)]))}const m=a(r,[["render",i]]);export{p as __pageData,m as default};
