<span class="line"><span style="color:#ABB2BF;">        &lt;</span><span style="color:#E06C75;">img</span><span style="color:#D19A66;"> class</span><span style="color:#ABB2BF;">=</span><span style="color:#98C379;">&quot;object-cover object-center rounded&quot;</span><span style="color:#D19A66;"> alt</span><span style="color:#ABB2BF;">=</span><span style="color:#98C379;">&quot;hero&quot;</span><span style="color:#D19A66;"> src</span><span style="color:#ABB2BF;">=</span><span style="color:#98C379;">&quot;../assets/pana.png&quot;</span><span style="color:#ABB2BF;">&gt;</span></span>
