import{_ as a,c as n,o as e,ah as l}from"./chunks/framework.DqD713j2.js";const p="/FrontEndLearnNotes/assets/image-20240219163945538.htyUMVGc.png",r="/FrontEndLearnNotes/assets/image-20240219163959902.BQlzf3Fd.png",o="/FrontEndLearnNotes/assets/image-20240219164612954.BWX2embV.png",i="/FrontEndLearnNotes/assets/image-20240219164701565.CZTujAOw.png",c="/FrontEndLearnNotes/assets/image-20240219164716918.y_JcEPRi.png",t="/FrontEndLearnNotes/assets/image-20240219164726260-1708332446889-3.C1S06jqw.png",d="/FrontEndLearnNotes/assets/image-20240219164738808.Dsnvvtct.png",b="/FrontEndLearnNotes/assets/image-20240219164748863.rmKvrxKs.png",u="/FrontEndLearnNotes/assets/image-20240219164758563.BYaR6uri.png",h="/FrontEndLearnNotes/assets/92bd924f9f13c0285ce8779de4c922a6.jmlALAXT.png",m="/FrontEndLearnNotes/assets/image-20240219164329783.DYm8iXhI.png",B=JSON.parse('{"title":"服务器运维和配置","description":"","frontmatter":{},"headers":[],"relativePath":"Document/服务器相关配置和运维/服务器运维和配置.md","filePath":"Document/服务器相关配置和运维/服务器运维和配置.md","lastUpdated":1750954157000}'),y={name:"Document/服务器相关配置和运维/服务器运维和配置.md"};function g(C,s,v,x,F,f){return e(),n("div",null,s[0]||(s[0]=[l('<h1 id="服务器运维和配置" tabindex="-1">服务器运维和配置 <a class="header-anchor" href="#服务器运维和配置" aria-label="Permalink to &quot;服务器运维和配置&quot;">​</a></h1><h2 id="一、服务器和域名购买" tabindex="-1">一、服务器和域名购买 <a class="header-anchor" href="#一、服务器和域名购买" aria-label="Permalink to &quot;一、服务器和域名购买&quot;">​</a></h2><h3 id="服务器服务商" tabindex="-1">服务器服务商 <a class="header-anchor" href="#服务器服务商" aria-label="Permalink to &quot;服务器服务商&quot;">​</a></h3><p>阿里云服务器：</p><p>腾讯云服务器：</p><p>百度云服务器：</p><p>华为云服务器：</p><h3 id="域名购买" tabindex="-1">域名购买 <a class="header-anchor" href="#域名购买" aria-label="Permalink to &quot;域名购买&quot;">​</a></h3><h2 id="二、认识服务器控制台菜单的信息" tabindex="-1">二、认识服务器控制台菜单的信息 <a class="header-anchor" href="#二、认识服务器控制台菜单的信息" aria-label="Permalink to &quot;二、认识服务器控制台菜单的信息&quot;">​</a></h2><p>概览</p><p>站点设置</p><p>安全</p><p>服务器运维</p><h2 id="三、服务端-linux-运维" tabindex="-1">三、服务端 Linux 运维 <a class="header-anchor" href="#三、服务端-linux-运维" aria-label="Permalink to &quot;三、服务端 Linux 运维&quot;">​</a></h2><h3 id="_0-认识-linux" tabindex="-1">0.认识 Linux <a class="header-anchor" href="#_0-认识-linux" aria-label="Permalink to &quot;0.认识 Linux&quot;">​</a></h3><p>Linux，全称 GNU/Linux，是一种免费使用和自由传播的类 UNIX 操作系统，其内核由林纳斯·本纳第克特·托瓦兹于 1991 年 10 月 5 日首次发布，它主要受到 Minix 和 Unix 思想的启发，是一个基于 POSIX 的多用户、多任务、支持多线程和多 CPU 的操作系统。</p><p>它能运行主要的 Unix 工具软件、应用程序和网络协议。它支持 32 位和 64 位硬件。Linux 继承了 Unix 以网络为核心的设计思想，是一个性能稳定的多用户网络操作系统。Linux 有上百种不同的发行版，如基于社区开发的 debian、archlinux，和基于商业开发的 Red Hat Enterprise Linux、SUSE、Oracle Linux 等。</p><h3 id="_1-连接-linux-服务器" tabindex="-1">1.连接 Linux 服务器 <a class="header-anchor" href="#_1-连接-linux-服务器" aria-label="Permalink to &quot;1.连接 Linux 服务器&quot;">​</a></h3><h4 id="_1-使用-ssh-连接服务器" tabindex="-1">1.使用 SSH 连接服务器 <a class="header-anchor" href="#_1-使用-ssh-连接服务器" aria-label="Permalink to &quot;1.使用 SSH 连接服务器&quot;">​</a></h4><p>window 安装 SSH（安装地址） <a href="http://www.mls-software.com/opensshd.html" target="_blank" rel="noreferrer">mls-software.com</a></p><p>Macbook 免安装自带</p><h4 id="_2-使用方式" tabindex="-1">2.使用方式 <a class="header-anchor" href="#_2-使用方式" aria-label="Permalink to &quot;2.使用方式&quot;">​</a></h4><p>打开终端</p><p>ssh 账号默认 root@服务器地址</p><p>例子：ssh root@123.456.789</p><p>然后选择 Y ，然后输入密码即可</p><p><img src="'+p+'" alt="image-20240219163945538"></p><p>显示这个代表连接成功</p><p><img src="'+r+'" alt="image-20240219163959902"></p><h3 id="_2-常用的-linux-命令" tabindex="-1">2.常用的 Linux 命令 <a class="header-anchor" href="#_2-常用的-linux-命令" aria-label="Permalink to &quot;2.常用的 Linux 命令&quot;">​</a></h3><h4 id="查看当前的目录的文件" tabindex="-1">查看当前的目录的文件 <a class="header-anchor" href="#查看当前的目录的文件" aria-label="Permalink to &quot;查看当前的目录的文件&quot;">​</a></h4><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki one-dark-pro vp-code" tabindex="0"><code><span class="line"><span style="color:#61AFEF;">ls</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><p>查看文件属性信息</p><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki one-dark-pro vp-code" tabindex="0"><code><span class="line"><span style="color:#61AFEF;">ls</span><span style="color:#D19A66;"> -l</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><p><img src="'+o+'" alt="image-20240219164612954"></p><h5 id="首字母作用" tabindex="-1">首字母作用 <a class="header-anchor" href="#首字母作用" aria-label="Permalink to &quot;首字母作用&quot;">​</a></h5><p>文件的开头对应是 -</p><p>目录开头对应的是 d</p><p>快捷方式对应的是 l</p><p>U 盘对应的是 b</p><p>鼠标键盘等硬件对应的 c</p><h4 id="linux-文件属性" tabindex="-1">Linux 文件属性 <a class="header-anchor" href="#linux-文件属性" aria-label="Permalink to &quot;Linux 文件属性&quot;">​</a></h4><p>每三个一组 rw-</p><p>-为缺少权限</p><p><img src="'+i+'" alt="image-20240219164701565"></p><p><img src="'+c+'" alt="image-20240219164716918"></p><p><img src="'+t+'" alt="image-20240219164726260"></p><p><img src="'+d+'" alt="image-20240219164738808"></p><p><img src="'+b+'" alt="image-20240219164748863"></p><p><img src="'+u+`" alt="image-20240219164758563"></p><h4 id="切换目录" tabindex="-1">切换目录 <a class="header-anchor" href="#切换目录" aria-label="Permalink to &quot;切换目录&quot;">​</a></h4><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki one-dark-pro vp-code" tabindex="0"><code><span class="line"><span style="color:#56B6C2;">cd</span><span style="color:#98C379;"> 目录路径</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><h4 id="linux-wget-命令用来下载软件" tabindex="-1">Linux wget 命令用来下载软件 <a class="header-anchor" href="#linux-wget-命令用来下载软件" aria-label="Permalink to &quot;Linux wget 命令用来下载软件&quot;">​</a></h4><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki one-dark-pro vp-code" tabindex="0"><code><span class="line"><span style="color:#61AFEF;">wget</span><span style="color:#98C379;"> https://cdn.npmmirror.com/binaries/node/v14.19.1/node-v14.19.1-linux-x64.tar.xz</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><h4 id="下载完成之后——解压" tabindex="-1">下载完成之后——解压 <a class="header-anchor" href="#下载完成之后——解压" aria-label="Permalink to &quot;下载完成之后——解压&quot;">​</a></h4><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki one-dark-pro vp-code" tabindex="0"><code><span class="line"><span style="color:#61AFEF;">使用tar</span><span style="color:#98C379;"> 命令</span></span>
<span class="line"><span style="color:#61AFEF;">-c:</span><span style="color:#98C379;"> 压缩</span></span>
<span class="line"><span style="color:#61AFEF;">-x：解压</span></span>
<span class="line"><span style="color:#61AFEF;">-t：查看内容</span></span>
<span class="line"><span style="color:#61AFEF;">-r：向压缩归档文件末尾追加文件</span></span>
<span class="line"><span style="color:#61AFEF;">-u：更新原压缩包中的文件</span></span>
<span class="line"></span>
<span class="line"><span style="color:#61AFEF;">这五个是独立的命令，压缩解压都要用到其中一个，可以和别的命令连用但只能用其中一个。下面的参数是根据需要在压缩或解压档案时可选的。</span></span>
<span class="line"></span>
<span class="line"><span style="color:#61AFEF;">-z：有gzip属性的</span></span>
<span class="line"><span style="color:#61AFEF;">-j：有bz2属性的</span></span>
<span class="line"><span style="color:#61AFEF;">-Z：有compress属性的</span></span>
<span class="line"><span style="color:#61AFEF;">-v：显示所有过程</span></span>
<span class="line"><span style="color:#61AFEF;">-O：将文件解开到标准输出</span></span>
<span class="line"></span>
<span class="line"><span style="color:#61AFEF;">下面的参数-f是必须的</span></span>
<span class="line"></span>
<span class="line"><span style="color:#61AFEF;">-f:</span><span style="color:#98C379;"> 使用档案名字，切记，这个参数是最后一个参数，后面只能接档案名。</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># tar -cf all.tar *.jpg</span></span>
<span class="line"><span style="color:#61AFEF;">这条命令是将所有.jpg的文件打成一个名为all.tar的包。-c是表示产生新的包，-f指定包的文件名。</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># tar -rf all.tar *.gif</span></span>
<span class="line"><span style="color:#61AFEF;">这条命令是将所有.gif的文件增加到all.tar的包里面去。-r是表示增加文件的意思。</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># tar -uf all.tar logo.gif</span></span>
<span class="line"><span style="color:#61AFEF;">这条命令是更新原来tar包all.tar中logo.gif文件，-u是表示更新文件的意思。</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># tar -tf all.tar</span></span>
<span class="line"><span style="color:#61AFEF;">这条命令是列出all.tar包中所有文件，-t是列出文件的意思</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># tar -xf all.tar</span></span>
<span class="line"><span style="color:#61AFEF;">这条命令是解出all.tar包中所有文件，-x是解开的意思</span></span>
<span class="line"></span>
<span class="line"><span style="color:#61AFEF;">压缩</span></span>
<span class="line"><span style="color:#61AFEF;">tar</span><span style="color:#98C379;"> –cvf</span><span style="color:#98C379;"> jpg.tar</span><span style="color:#E5C07B;"> *</span><span style="color:#98C379;">.jpg</span><span style="color:#98C379;"> //将目录里所有jpg文件打包成tar.jpg</span></span>
<span class="line"><span style="color:#61AFEF;">tar</span><span style="color:#98C379;"> –czf</span><span style="color:#98C379;"> jpg.tar.gz</span><span style="color:#E5C07B;"> *</span><span style="color:#98C379;">.jpg</span><span style="color:#98C379;">   //将目录里所有jpg文件打包成jpg.tar后，并且将其用gzip压缩，生成一个gzip压缩过的包，命名为jpg.tar.gz</span></span>
<span class="line"><span style="color:#61AFEF;">tar</span><span style="color:#98C379;"> –cjf</span><span style="color:#98C379;"> jpg.tar.bz2</span><span style="color:#E5C07B;"> *</span><span style="color:#98C379;">.jpg</span><span style="color:#98C379;"> //将目录里所有jpg文件打包成jpg.tar后，并且将其用bzip2压缩，生成一个bzip2压缩过的包，命名为jpg.tar.bz2</span></span>
<span class="line"><span style="color:#61AFEF;">tar</span><span style="color:#98C379;"> –cZf</span><span style="color:#98C379;"> jpg.tar.Z</span><span style="color:#E5C07B;"> *</span><span style="color:#98C379;">.jpg</span><span style="color:#98C379;">   //将目录里所有jpg文件打包成jpg.tar后，并且将其用compress压缩，生成一个umcompress压缩过的包，命名为jpg.tar.Z</span></span>
<span class="line"><span style="color:#61AFEF;">rar</span><span style="color:#98C379;"> a</span><span style="color:#98C379;"> jpg.rar</span><span style="color:#E5C07B;"> *</span><span style="color:#98C379;">.jpg</span><span style="color:#98C379;"> //rar格式的压缩，需要先下载rar</span><span style="color:#98C379;"> for</span><span style="color:#98C379;"> Linux</span></span>
<span class="line"><span style="color:#61AFEF;">zip</span><span style="color:#98C379;"> jpg.zip</span><span style="color:#E5C07B;"> *</span><span style="color:#98C379;">.jpg</span><span style="color:#98C379;"> //zip格式的压缩，需要先下载zip</span><span style="color:#98C379;"> for</span><span style="color:#98C379;"> linux</span></span>
<span class="line"></span>
<span class="line"><span style="color:#61AFEF;">解压</span></span>
<span class="line"><span style="color:#61AFEF;">tar</span><span style="color:#98C379;"> –xvf</span><span style="color:#98C379;"> file.tar</span><span style="color:#98C379;"> //解压</span><span style="color:#98C379;"> tar包</span></span>
<span class="line"><span style="color:#61AFEF;">tar</span><span style="color:#D19A66;"> -xzvf</span><span style="color:#98C379;"> file.tar.gz</span><span style="color:#98C379;"> //解压tar.gz</span></span>
<span class="line"><span style="color:#61AFEF;">tar</span><span style="color:#D19A66;"> -xjvf</span><span style="color:#98C379;"> file.tar.bz2</span><span style="color:#98C379;">   //解压</span><span style="color:#98C379;"> tar.bz2</span></span>
<span class="line"><span style="color:#61AFEF;">tar</span><span style="color:#98C379;"> –xZvf</span><span style="color:#98C379;"> file.tar.Z</span><span style="color:#98C379;">   //解压tar.Z</span></span>
<span class="line"><span style="color:#61AFEF;">unrar</span><span style="color:#98C379;"> e</span><span style="color:#98C379;"> file.rar</span><span style="color:#98C379;"> //解压rar</span></span>
<span class="line"><span style="color:#61AFEF;">unzip</span><span style="color:#98C379;"> file.zip</span><span style="color:#98C379;"> //解压zip</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br><span class="line-number">9</span><br><span class="line-number">10</span><br><span class="line-number">11</span><br><span class="line-number">12</span><br><span class="line-number">13</span><br><span class="line-number">14</span><br><span class="line-number">15</span><br><span class="line-number">16</span><br><span class="line-number">17</span><br><span class="line-number">18</span><br><span class="line-number">19</span><br><span class="line-number">20</span><br><span class="line-number">21</span><br><span class="line-number">22</span><br><span class="line-number">23</span><br><span class="line-number">24</span><br><span class="line-number">25</span><br><span class="line-number">26</span><br><span class="line-number">27</span><br><span class="line-number">28</span><br><span class="line-number">29</span><br><span class="line-number">30</span><br><span class="line-number">31</span><br><span class="line-number">32</span><br><span class="line-number">33</span><br><span class="line-number">34</span><br><span class="line-number">35</span><br><span class="line-number">36</span><br><span class="line-number">37</span><br><span class="line-number">38</span><br><span class="line-number">39</span><br><span class="line-number">40</span><br><span class="line-number">41</span><br><span class="line-number">42</span><br><span class="line-number">43</span><br><span class="line-number">44</span><br><span class="line-number">45</span><br><span class="line-number">46</span><br><span class="line-number">47</span><br><span class="line-number">48</span><br><span class="line-number">49</span><br></div></div><p>我们执行</p><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki one-dark-pro vp-code" tabindex="0"><code><span class="line"><span style="color:#61AFEF;">tar</span><span style="color:#D19A66;"> -xvf</span><span style="color:#98C379;"> node-v14.19.1-linux-x64.tar.xz</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><h4 id="linux-权限" tabindex="-1">Linux 权限 <a class="header-anchor" href="#linux-权限" aria-label="Permalink to &quot;Linux 权限&quot;">​</a></h4><p>查看查看文件信息</p><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki one-dark-pro vp-code" tabindex="0"><code><span class="line"><span style="color:#61AFEF;">ls</span><span style="color:#D19A66;"> -l</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><p>RWX 分别是可读 可写 可执行权限</p><p>这个权限我们可以修改使用 chmod 777 文件名</p><p>我们先学习一组规律</p><ul><li>读------R 对应数字 4</li><li>写------W 对应数字 2</li><li>可执行-----X 对应数字 1</li></ul><p>所以 7 就是可读可写可执行权限</p><p><img src="`+h+`" alt="img"></p><p>我们可以在 root 账户下创建一个用户，测试一下 使用 adduser 账号 passwd 给用户设置密码</p><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki one-dark-pro vp-code" tabindex="0"><code><span class="line"><span style="color:#7F848E;font-style:italic;"># 创建账户</span></span>
<span class="line"><span style="color:#61AFEF;">adduser</span><span style="color:#98C379;"> hehe</span></span>
<span class="line"><span style="color:#61AFEF;">passwd</span><span style="color:#98C379;"> hehe</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br></div></div><p>重新使用这个新账户连接 sh，使用 cmd</p><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki one-dark-pro vp-code" tabindex="0"><code><span class="line"><span style="color:#61AFEF;">sh</span><span style="color:#98C379;"> hehe@公网ip</span></span>
<span class="line"><span style="color:#61AFEF;">然后输入密码：</span></span>
<span class="line"><span style="color:#61AFEF;">hehe</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br></div></div><p>hehe 用户下的看看是否有权限：无权限 的用户将无法读写</p><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki one-dark-pro vp-code" tabindex="0"><code><span class="line"><span style="color:#56B6C2;">echo</span><span style="color:#98C379;"> hell</span><span style="color:#ABB2BF;">o&gt;</span><span style="color:#98C379;">index.txt</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 没有权限出现：-bash:  index.txt: Permission denied</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br></div></div><p>root 用户下增加这个账户的权限：我们 root 账号把 index.txt 设置 700</p><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki one-dark-pro vp-code" tabindex="0"><code><span class="line"><span style="color:#61AFEF;">chmod</span><span style="color:#D19A66;"> 700</span><span style="color:#98C379;"> index.txt</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><p>再次在 hehe 用户下的看看是否有权限</p><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki one-dark-pro vp-code" tabindex="0"><code><span class="line"><span style="color:#56B6C2;">echo</span><span style="color:#98C379;"> heh</span><span style="color:#ABB2BF;">e&gt; </span><span style="color:#98C379;">index.txt</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><h3 id="_3-使用命令配置-linux-服务器" tabindex="-1">3.使用命令配置 Linux 服务器 <a class="header-anchor" href="#_3-使用命令配置-linux-服务器" aria-label="Permalink to &quot;3.使用命令配置 Linux 服务器&quot;">​</a></h3><h4 id="配置环境变量" tabindex="-1">配置环境变量 <a class="header-anchor" href="#配置环境变量" aria-label="Permalink to &quot;配置环境变量&quot;">​</a></h4><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki one-dark-pro vp-code" tabindex="0"><code><span class="line"><span style="color:#56B6C2;">pwd</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><p>命令用于显示工作目录</p><p><img src="`+m+`" alt="image-20240219164329783"></p><p>使用 pwd 命令获取 node bin 的位置</p><p>前往跟目录下面的 etc/profile 此文件涉及系统的环境，即环境变量相关。这里修改会对所有用户起作用</p><p>使用 vi 命令编辑该文件</p><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki one-dark-pro vp-code" tabindex="0"><code><span class="line"><span style="color:#61AFEF;">vi</span><span style="color:#98C379;"> profile</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><p>然后按一下 I 键 进入编辑模式</p><p>在最后一行添加</p><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki one-dark-pro vp-code" tabindex="0"><code><span class="line"><span style="color:#C678DD;">export</span><span style="color:#E06C75;"> PATH</span><span style="color:#56B6C2;">=</span><span style="color:#E06C75;">$PATH</span><span style="color:#ABB2BF;">:你的目录/</span><span style="color:#E06C75;">node-v14</span><span style="color:#ABB2BF;">.</span><span style="color:#E06C75;">19</span><span style="color:#ABB2BF;">.</span><span style="color:#E06C75;">1-linux-x64</span><span style="color:#ABB2BF;">/</span><span style="color:#E06C75;">bin</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><p>然后执行 <code>source profile</code> 或者重启 linux</p><p>配置淘宝代理</p><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki one-dark-pro vp-code" tabindex="0"><code><span class="line"><span style="color:#61AFEF;">npm</span><span style="color:#98C379;"> config</span><span style="color:#98C379;"> set</span><span style="color:#98C379;"> registry</span><span style="color:#98C379;"> https://registry.npm.taobao.org</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><h3 id="_4-未配置域名是可以使用公网-ip-访问地址" tabindex="-1">4.未配置域名是可以使用公网 IP 访问地址 <a class="header-anchor" href="#_4-未配置域名是可以使用公网-ip-访问地址" aria-label="Permalink to &quot;4.未配置域名是可以使用公网 IP 访问地址&quot;">​</a></h3><p>例如服务器公网 IP/接口名：</p><h2 id="四、服务器软件配置" tabindex="-1">四、服务器软件配置 <a class="header-anchor" href="#四、服务器软件配置" aria-label="Permalink to &quot;四、服务器软件配置&quot;">​</a></h2><h2 id="linux-安装-nginx" tabindex="-1">Linux 安装 Nginx <a class="header-anchor" href="#linux-安装-nginx" aria-label="Permalink to &quot;Linux 安装 Nginx&quot;">​</a></h2><h3 id="_1、安装-gcc" tabindex="-1">1、安装 gcc <a class="header-anchor" href="#_1、安装-gcc" aria-label="Permalink to &quot;1、安装 gcc&quot;">​</a></h3><p>安装 nginx 需要先将官网下载的源码进行编译，编译依赖 gcc 环境，如果没有 gcc 环境，则需要安装：</p><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki one-dark-pro vp-code" tabindex="0"><code><span class="line"><span style="color:#61AFEF;">yum</span><span style="color:#98C379;"> install</span><span style="color:#98C379;"> gcc-c++</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><h3 id="_2、pcre-pcre-devel-安装" tabindex="-1">2、PCRE pcre-devel 安装 <a class="header-anchor" href="#_2、pcre-pcre-devel-安装" aria-label="Permalink to &quot;2、PCRE pcre-devel 安装&quot;">​</a></h3><p>PCRE(Perl Compatible Regular Expressions) 是一个 Perl 库，包括 perl 兼容的正则表达式库。nginx 的 http 模块使用 pcre 来解析正则表达式，所以需要在 linux 上安装 pcre 库，pcre-devel 是使用 pcre 开发的一个二次开发库。nginx 也需要此库。命令：</p><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki one-dark-pro vp-code" tabindex="0"><code><span class="line"><span style="color:#61AFEF;">yum</span><span style="color:#98C379;"> install</span><span style="color:#D19A66;"> -y</span><span style="color:#98C379;"> pcre</span><span style="color:#98C379;"> pcre-devel</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><h3 id="_3、zlib-安装" tabindex="-1">3、zlib 安装 <a class="header-anchor" href="#_3、zlib-安装" aria-label="Permalink to &quot;3、zlib 安装&quot;">​</a></h3><p>zlib 库提供了很多种压缩和解压缩的方式， nginx 使用 zlib 对 http 包的内容进行 gzip ，所以需要在 Centos 上安装 zlib 库。</p><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki one-dark-pro vp-code" tabindex="0"><code><span class="line"><span style="color:#61AFEF;">yum</span><span style="color:#98C379;"> install</span><span style="color:#D19A66;"> -y</span><span style="color:#98C379;"> zlib</span><span style="color:#98C379;"> zlib-devel</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><h3 id="_4、openssl-安装" tabindex="-1">4、OpenSSL 安装 <a class="header-anchor" href="#_4、openssl-安装" aria-label="Permalink to &quot;4、OpenSSL 安装&quot;">​</a></h3><p>OpenSSL 是一个强大的安全套接字层密码库，囊括主要的密码算法、常用的密钥和证书封装管理功能及 SSL 协议，并提供丰富的应用程序供测试或其它目的使用。</p><p>nginx 不仅支持 http 协议，还支持 https（即在 ssl 协议上传输 http），所以需要在 Centos 安装 OpenSSL 库。</p><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki one-dark-pro vp-code" tabindex="0"><code><span class="line"><span style="color:#61AFEF;">yum</span><span style="color:#98C379;"> install</span><span style="color:#D19A66;"> -y</span><span style="color:#98C379;"> openssl</span><span style="color:#98C379;"> openssl-devel</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><h3 id="_5-下载-nginx" tabindex="-1">5.下载 Nginx <a class="header-anchor" href="#_5-下载-nginx" aria-label="Permalink to &quot;5.下载 Nginx&quot;">​</a></h3><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki one-dark-pro vp-code" tabindex="0"><code><span class="line"><span style="color:#61AFEF;">wget</span><span style="color:#98C379;"> https://nginx.org/download/nginx-1.19.9.tar.gz</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><h3 id="_6-解压-nginx" tabindex="-1">6.解压 nginx <a class="header-anchor" href="#_6-解压-nginx" aria-label="Permalink to &quot;6.解压 nginx&quot;">​</a></h3><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki one-dark-pro vp-code" tabindex="0"><code><span class="line"><span style="color:#61AFEF;">tar</span><span style="color:#D19A66;"> -zxvf</span><span style="color:#98C379;"> nginx-1.19.9.tar.gz</span></span>
<span class="line"><span style="color:#56B6C2;">cd</span><span style="color:#98C379;"> nginx-1.19.9</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br></div></div><h3 id="_7-执行-nginx-configure-文件" tabindex="-1">7.执行 nginx-configure 文件 <a class="header-anchor" href="#_7-执行-nginx-configure-文件" aria-label="Permalink to &quot;7.执行 nginx-configure 文件&quot;">​</a></h3><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki one-dark-pro vp-code" tabindex="0"><code><span class="line"><span style="color:#61AFEF;">ls</span></span>
<span class="line"><span style="color:#61AFEF;">./configure</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br></div></div><h3 id="_8-make-命令编译" tabindex="-1">8.make 命令编译 <a class="header-anchor" href="#_8-make-命令编译" aria-label="Permalink to &quot;8.make 命令编译&quot;">​</a></h3><p>执行完后会有一个 MakeFile 文件夹</p><p>make 是一个命令工具，它解释 Makefile 中的指令（应该说是规则）。在 Makefile 文件中描述了整个工程所有文件的编译顺序、编译规则</p><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki one-dark-pro vp-code" tabindex="0"><code><span class="line"><span style="color:#61AFEF;">make</span></span>
<span class="line"><span style="color:#61AFEF;">make</span><span style="color:#98C379;"> install</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br></div></div><h3 id="_9-查询-nginx-安装目录" tabindex="-1">9.查询 nginx 安装目录 <a class="header-anchor" href="#_9-查询-nginx-安装目录" aria-label="Permalink to &quot;9.查询 nginx 安装目录&quot;">​</a></h3><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki one-dark-pro vp-code" tabindex="0"><code><span class="line"><span style="color:#61AFEF;">whereis</span><span style="color:#98C379;"> nginx</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><h3 id="_10-进入安装目录执行-nginx" tabindex="-1">10.进入安装目录执行 nginx <a class="header-anchor" href="#_10-进入安装目录执行-nginx" aria-label="Permalink to &quot;10.进入安装目录执行 nginx&quot;">​</a></h3><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki one-dark-pro vp-code" tabindex="0"><code><span class="line"><span style="color:#56B6C2;">cd</span><span style="color:#98C379;"> usr</span><span style="color:#98C379;"> local/nginx</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><p>前往安装目录找到 sbin 执行 nginx</p><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki one-dark-pro vp-code" tabindex="0"><code><span class="line"><span style="color:#61AFEF;">./nginx</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><h3 id="yum-install-404-解决方案" tabindex="-1">yum install 404 解决方案 <a class="header-anchor" href="#yum-install-404-解决方案" aria-label="Permalink to &quot;yum install 404 解决方案&quot;">​</a></h3><p>1、进入配置文件内，删除所有的.repo 文件（也可以备份）</p><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki one-dark-pro vp-code" tabindex="0"><code><span class="line"><span style="color:#7F848E;font-style:italic;">#进入配置文件夹</span></span>
<span class="line"><span style="color:#56B6C2;">cd</span><span style="color:#98C379;"> /etc/yum.repos.d/</span></span>
<span class="line"><span style="color:#7F848E;font-style:italic;">#删除旧的配置文件</span></span>
<span class="line"><span style="color:#61AFEF;">rm</span><span style="color:#E5C07B;"> *</span><span style="color:#98C379;">.repo</span></span>
<span class="line"><span style="color:#7F848E;font-style:italic;">#输入“y”回车确认</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br></div></div><p>ls 确保该目录下的.repo 文件已完全删除 下载可以用的.repo 文件</p><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki one-dark-pro vp-code" tabindex="0"><code><span class="line"><span style="color:#61AFEF;">wget</span><span style="color:#D19A66;"> -O</span><span style="color:#98C379;"> /etc/yum.repos.d/CentOS-Base.repo</span><span style="color:#98C379;"> https://mirrors.aliyun.com/repo/Centos-vault-8.5.2111.repo</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><p>如果没有安装 wget，也可以用下面命令：</p><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki one-dark-pro vp-code" tabindex="0"><code><span class="line"><span style="color:#61AFEF;">curl</span><span style="color:#D19A66;"> -o</span><span style="color:#98C379;"> /etc/yum.repos.d/CentOS-Base.repo</span><span style="color:#98C379;"> https://mirrors.aliyun.com/repo/Centos-vault-8.5.2111.repo</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><p>运行 yum makecache 生成缓存</p><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki one-dark-pro vp-code" tabindex="0"><code><span class="line"><span style="color:#61AFEF;">yum</span><span style="color:#98C379;"> makecache</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><h3 id="linux-防火墙" tabindex="-1">Linux 防火墙 <a class="header-anchor" href="#linux-防火墙" aria-label="Permalink to &quot;Linux 防火墙&quot;">​</a></h3><h4 id="_1、firewalld-的基本使用" tabindex="-1">1、firewalld 的基本使用 <a class="header-anchor" href="#_1、firewalld-的基本使用" aria-label="Permalink to &quot;1、firewalld 的基本使用&quot;">​</a></h4><p>启动： systemctl start firewalld</p><p>查看状态： systemctl status firewalld</p><p>停止：systemctl stop firewalld</p><p>禁用：systemctl disable firewalld</p><h4 id="_2-systemctl-是-centos7-的服务管理工具中主要的工具-它融合之前-service-和-chkconfig-的功能于一体" tabindex="-1">2.systemctl 是 CentOS7 的服务管理工具中主要的工具，它融合之前 service 和 chkconfig 的功能于一体 <a class="header-anchor" href="#_2-systemctl-是-centos7-的服务管理工具中主要的工具-它融合之前-service-和-chkconfig-的功能于一体" aria-label="Permalink to &quot;2.systemctl 是 CentOS7 的服务管理工具中主要的工具，它融合之前 service 和 chkconfig 的功能于一体&quot;">​</a></h4><p>启动一个服务：systemctl start firewalld.service 关闭一个服务：systemctl stop firewalld.service 重启一个服务：systemctl restart firewalld.service 显示一个服务的状态：systemctl status firewalld.service 在开机时启用一个服务：systemctl enable firewalld.service 在开机时禁用一个服务：systemctl disable firewalld.service 查看服务是否开机启动：systemctl is-enabled firewalld.service 查看已启动的服务列表：systemctl list-unit-files|grep enabled 查看启动失败的服务列表：systemctl --failed</p><h4 id="_3-配置-firewalld-cmd" tabindex="-1">3.配置 firewalld-cmd <a class="header-anchor" href="#_3-配置-firewalld-cmd" aria-label="Permalink to &quot;3.配置 firewalld-cmd&quot;">​</a></h4><p>查看版本： firewall-cmd --version</p><p>查看帮助： firewall-cmd --help</p><p>显示状态： firewall-cmd --state</p><p>查看所有打开的端口： firewall-cmd --zone=public --list-ports</p><p>更新防火墙规则： firewall-cmd --reload</p><p>查看区域信息: firewall-cmd --get-active-zones</p><p>查看指定接口所属区域： firewall-cmd --get-zone-of-interface=eth0</p><p>拒绝所有包：firewall-cmd --panic-on</p><p>取消拒绝状态： firewall-cmd --panic-off</p><p>查看是否拒绝： firewall-cmd --query-panic</p><h4 id="那怎么开启一个端口呢" tabindex="-1">那怎么开启一个端口呢 <a class="header-anchor" href="#那怎么开启一个端口呢" aria-label="Permalink to &quot;那怎么开启一个端口呢&quot;">​</a></h4><h5 id="添加" tabindex="-1">添加 <a class="header-anchor" href="#添加" aria-label="Permalink to &quot;添加&quot;">​</a></h5><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki one-dark-pro vp-code" tabindex="0"><code><span class="line"><span style="color:#61AFEF;">firewall-cmd</span><span style="color:#D19A66;"> --zone=public</span><span style="color:#D19A66;"> --add-port=80/tcp</span><span style="color:#D19A66;"> --permanent</span><span style="color:#98C379;">    （--permanent永久生效，没有此参数重启后失效）</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><h5 id="重新载入" tabindex="-1">重新载入 <a class="header-anchor" href="#重新载入" aria-label="Permalink to &quot;重新载入&quot;">​</a></h5><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki one-dark-pro vp-code" tabindex="0"><code><span class="line"><span style="color:#61AFEF;">firewall-cmd</span><span style="color:#D19A66;"> --reload</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><h5 id="查看" tabindex="-1">查看 <a class="header-anchor" href="#查看" aria-label="Permalink to &quot;查看&quot;">​</a></h5><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki one-dark-pro vp-code" tabindex="0"><code><span class="line"><span style="color:#61AFEF;">firewall-cmd</span><span style="color:#D19A66;"> --zone=</span><span style="color:#98C379;"> public</span><span style="color:#D19A66;"> --query-port=80/tcp</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><h5 id="删除" tabindex="-1">删除 <a class="header-anchor" href="#删除" aria-label="Permalink to &quot;删除&quot;">​</a></h5><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki one-dark-pro vp-code" tabindex="0"><code><span class="line"><span style="color:#61AFEF;">firewall-cmd</span><span style="color:#D19A66;"> --zone=</span><span style="color:#98C379;"> public</span><span style="color:#D19A66;"> --remove-port=80/tcp</span><span style="color:#D19A66;"> --permanent</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><h2 id="五、服务器部署项目" tabindex="-1">五、服务器部署项目 <a class="header-anchor" href="#五、服务器部署项目" aria-label="Permalink to &quot;五、服务器部署项目&quot;">​</a></h2><h3 id="_1-域名备案解析" tabindex="-1">1.域名备案解析 <a class="header-anchor" href="#_1-域名备案解析" aria-label="Permalink to &quot;1.域名备案解析&quot;">​</a></h3><p>只有中国大陆的服务器需要域名备案，而香港或者其他国家的服务器不需要备案域名</p><h3 id="_2-部署二级域名" tabindex="-1">2.部署二级域名 <a class="header-anchor" href="#_2-部署二级域名" aria-label="Permalink to &quot;2.部署二级域名&quot;">​</a></h3><p>Nginx 的 conf 配置</p><div class="language-json line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">json</span><pre class="shiki one-dark-pro vp-code" tabindex="0"><code><span class="line"><span style="color:#ABB2BF;"># 把二级域名部署到这个vhosts文件夹</span></span>
<span class="line"><span style="color:#ABB2BF;">include /etc/nginx/vhosts</span><span style="color:#7F848E;font-style:italic;">/*.conf;</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br></div></div><h3 id="_3-阿里主机绑定多个域名" tabindex="-1">3.阿里主机绑定多个域名 <a class="header-anchor" href="#_3-阿里主机绑定多个域名" aria-label="Permalink to &quot;3.阿里主机绑定多个域名&quot;">​</a></h3><p>域名在阿里云备案，需要解析到阿里主机或服务器，阿里云的服务器又不便宜只有新用户才便宜，又不想域名掉备案又不想花重金在阿里买服务器怎么搞呢？</p><p>买阿里云的主机，但是有多个域名一个主机怎么实现加载不同的内容呢，今天这个代码是通过 ChatGPT 做出来的。</p><p>1.实现了三个域名加载不同的内容。对应域名加载对应 HTML。</p><p>2.请注意，在实际使用时，需要根据实际情况进行修改和适当的调整。</p><p>3.首先在虚拟机根目录创建一个 index.php 和三个 html 并命名为域名前缀。</p><p>4.复制下面代码到 index.php 文件中，并修改文件中的域名为自己的。</p><div class="language-php line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">php</span><pre class="shiki one-dark-pro vp-code" tabindex="0"><code><span class="line"><span style="color:#56B6C2;">&lt;</span><span style="color:#C678DD;">?</span><span style="color:#ABB2BF;">php</span></span>
<span class="line"></span>
<span class="line"><span style="color:#E06C75;">$host</span><span style="color:#56B6C2;"> =</span><span style="color:#E06C75;"> $_SERVER</span><span style="color:#ABB2BF;">[</span><span style="color:#98C379;">&#39;HTTP_HOST&#39;</span><span style="color:#ABB2BF;">];</span></span>
<span class="line"></span>
<span class="line"><span style="color:#C678DD;">switch</span><span style="color:#ABB2BF;"> (</span><span style="color:#E06C75;">$host</span><span style="color:#ABB2BF;">) {</span></span>
<span class="line"><span style="color:#C678DD;">    case</span><span style="color:#98C379;"> &#39;www.xx8g.com&#39;</span><span style="color:#ABB2BF;">:</span></span>
<span class="line"><span style="color:#7F848E;font-style:italic;">        // 加载 xiaogao1.html 页面</span></span>
<span class="line"><span style="color:#C678DD;">        include</span><span style="color:#98C379;"> &#39;xiaogao1.html&#39;</span><span style="color:#ABB2BF;">;</span></span>
<span class="line"><span style="color:#C678DD;">        break</span><span style="color:#ABB2BF;">;</span></span>
<span class="line"><span style="color:#C678DD;">    case</span><span style="color:#98C379;"> &#39;www.xx8g2.cn&#39;</span><span style="color:#ABB2BF;">:</span></span>
<span class="line"><span style="color:#7F848E;font-style:italic;">        // 加载 xiaogao2.html 页面</span></span>
<span class="line"><span style="color:#C678DD;">        include</span><span style="color:#98C379;"> &#39;xiaogao2.html&#39;</span><span style="color:#ABB2BF;">;</span></span>
<span class="line"><span style="color:#C678DD;">        break</span><span style="color:#ABB2BF;">;</span></span>
<span class="line"><span style="color:#C678DD;">    case</span><span style="color:#98C379;"> &#39;www.xx8g3.cn&#39;</span><span style="color:#ABB2BF;">:</span></span>
<span class="line"><span style="color:#7F848E;font-style:italic;">        // 加载 xiaogao3.html 页面</span></span>
<span class="line"><span style="color:#C678DD;">        include</span><span style="color:#98C379;"> &#39;xiaogao3.html&#39;</span><span style="color:#ABB2BF;">;</span></span>
<span class="line"><span style="color:#C678DD;">        break</span><span style="color:#ABB2BF;">;</span></span>
<span class="line"><span style="color:#C678DD;">    default</span><span style="color:#ABB2BF;">:</span></span>
<span class="line"><span style="color:#7F848E;font-style:italic;">        // 默认情况下返回一个 404 页面</span></span>
<span class="line"><span style="color:#56B6C2;">        header</span><span style="color:#ABB2BF;">(</span><span style="color:#98C379;">&#39;HTTP/1.1 404 Not Found&#39;</span><span style="color:#ABB2BF;">);</span></span>
<span class="line"><span style="color:#56B6C2;">        echo</span><span style="color:#98C379;"> &#39;&lt;html&gt;&lt;body&gt;&#39;</span><span style="color:#ABB2BF;">;</span></span>
<span class="line"><span style="color:#56B6C2;">        echo</span><span style="color:#98C379;"> &#39;&lt;h1&gt;404 Not Found&lt;/h1&gt;&#39;</span><span style="color:#ABB2BF;">;</span></span>
<span class="line"><span style="color:#56B6C2;">        echo</span><span style="color:#98C379;"> &#39;&lt;p&gt;The requested URL &#39;</span><span style="color:#ABB2BF;"> .</span><span style="color:#E06C75;"> $_SERVER</span><span style="color:#ABB2BF;">[</span><span style="color:#98C379;">&#39;REQUEST_URI&#39;</span><span style="color:#ABB2BF;">] </span><span style="color:#ABB2BF;">.</span><span style="color:#98C379;"> &#39; was not found on this server.&lt;/p&gt;&#39;</span><span style="color:#ABB2BF;">;</span></span>
<span class="line"><span style="color:#56B6C2;">        echo</span><span style="color:#98C379;"> &#39;&lt;/body&gt;&lt;/html&gt;&#39;</span><span style="color:#ABB2BF;">;</span></span>
<span class="line"><span style="color:#C678DD;">        break</span><span style="color:#ABB2BF;">;</span></span>
<span class="line"><span style="color:#ABB2BF;">}</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br><span class="line-number">9</span><br><span class="line-number">10</span><br><span class="line-number">11</span><br><span class="line-number">12</span><br><span class="line-number">13</span><br><span class="line-number">14</span><br><span class="line-number">15</span><br><span class="line-number">16</span><br><span class="line-number">17</span><br><span class="line-number">18</span><br><span class="line-number">19</span><br><span class="line-number">20</span><br><span class="line-number">21</span><br><span class="line-number">22</span><br><span class="line-number">23</span><br><span class="line-number">24</span><br><span class="line-number">25</span><br><span class="line-number">26</span><br></div></div><p>5.然后打开 html 修改要显示的内容即可，还有另一种实现的方法，使用伪静态，但是阿里云主机不支持 server 命令。</p>`,177)]))}const A=a(y,[["render",g]]);export{B as __pageData,A as default};
